class.cytable@tag.tr[1:20]@tag.a[1]
<js>
list=result.toArray();
json=[];
for(i in list){
id=String(list[i].attr("href")).match(/(\d+)/)[1];
url="http://www.jjwxc.net/onebook.php?novelid="+id;
html=String(java.ajax(url));
java.setContent(html);
J=org.jsoup.Jsoup.parse(html);

des=String(J.select("span[style='color:#F98C4D']").text()).replace(/立意：/,'\n◎ ').replace(/一句话简介：/,'◎ ')+"\n"+java.getElement("id.novelintro");
title=J.select('h1[itemprop="name"]').text();
author=J.select('span[itemprop="author"]').text();
cover=J.select(".noveldefaultimage").attr("src");
cat=String(J.select('a[style="text-decoration:none;color: red;"]').text()).replace(/\s/g,',');
status=J.select('span[itemprop="updataStatus"]').text();
size=String(J.select('span[itemprop="wordCount"]').text()).replace(/字/,'');
n=String(java.getElement('@@id.oneboolt@tag.tr.-2@span').text()).replace(/\n/,'•').replace(/\*最新更新/,'').replace(/(.*?)\[VIP\]/,'❍·$1');
num=java.getElement('@@id.oneboolt@tag.tr.-2@tag.td.0').text();
genre=J.select('[itemprop="genre"]').text().split('-')[1];
json.push({
title:title,
author:author,
cat:status+","+cat+','+genre,
size:size,
url:"http://app-cdn.jjwxc.net/androidapi/novelbasicinfo?novelId="+id,
des:des,
new:String(num+"、"+n).replace(/(\d+、)❍·/,'❍·$1'),
cover:cover
})
}
json
</js>

